'use client';

import React, {useEffect, useState} from 'react';
import Image from 'next/image';

interface SlideShowProps {
    images: { src: string; alt?: string; width: number; height: number }[];
    interval?: number;
}

const SlideShow: React.FC<SlideShowProps> = ({images, interval = 4000}) => {
    const [current, setCurrent] = useState(0);
    const [fade, setFade] = useState(true);

    useEffect(() => {
        const timeout = setTimeout(() => {
            setFade(false);
            setTimeout(() => {
                setCurrent((prev) => (prev + 1) % images.length);
                setFade(true);
            }, 500); // fade duration
        }, interval);
        return () => clearTimeout(timeout);
    }, [current, images.length, interval]);

    return (
        <div className="slideshow-container"
             style={{position: 'relative', width: images[0].width, height: images[0].height}}>
            {images.map((img, idx) => (
                <div
                    key={img.src}
                    className="slideshow-image"
                    style={{
                        opacity: idx === current && fade ? 1 : 0,
                        transition: 'opacity 0.5s ease',
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                    }}
                >
                    <Image src={img.src} alt={img.alt || ''} width={img.width} height={img.height}
                           style={{objectFit: 'cover', width: '100%', height: '100%'}}/>
                </div>
            ))}
        </div>
    );
};

export default SlideShow;

'use client';

import React from 'react';
import Image from 'next/image';
import Carousel from 'react-material-ui-carousel';

interface SlideShowProps {
    images: { src: string; alt?: string; width: number; height: number }[];
    interval?: number;
}

const SlideShow: React.FC<SlideShowProps> = ({images, interval = 4000}) => {
    return (
        <Carousel
            height={images[0]?.height || 400}
            autoPlay
            interval={interval}
            indicators={true}
            navButtonsAlwaysInvisible={false}
            animation="fade"
        >
            {images.map((img) => (
                <div key={img.src} style={{ width: img.width, height: img.height, position: 'relative' }}>
                    <Image src={img.src} alt={img.alt || ''} width={img.width} height={img.height} style={{ objectFit: 'cover', width: '100%', height: '100%' }} />
                </div>
            ))}
        </Carousel>
    );
};

export default SlideShow;
