import React, { useRef, useEffect, useState, ReactNode } from 'react';

interface ZoomInWrapperProps {
  children: ReactNode;
  className?: string;
}

const ZoomInWrapper: React.FC<ZoomInWrapperProps> = ({ children, className }) => {
  const ref = useRef<HTMLDivElement>(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const node = ref.current;
    if (!node) return;
    const observer = new window.IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          observer.disconnect();
        }
      },
      { threshold: 0.1 }
    );
    observer.observe(node);
    return () => observer.disconnect();
  }, []);

  return (
    <div
      ref={ref}
      className={
        `${className ? className + ' ' : ''}` +
        (isVisible ? 'zoom-in-animate' : 'zoom-in-init')
      }
    >
      {children}
    </div>
  );
};

export default ZoomInWrapper;

.animation-method-zoom-in {
  opacity: 0;
  transform: scale(0.95);
  transition: opacity 0.6s cubic-bezier(0.4,0,0.2,1), transform 0.6s cubic-bezier(0.4,0,0.2,1);
}

.animation-method-zoom-in.animation-active {
  opacity: 1;
  transform: scale(1);
}

.animation-method-zoom-in.animation-inactive {
  opacity: 0;
  transform: scale(0.95);
}

import React from 'react';
import Carousel from 'react-material-ui-carousel';
import { Paper, Button } from '@mui/material';

export interface CarouselItem {
  title: string;
  description: string;
  image?: string;
  buttonText?: string;
  onClick?: () => void;
}

interface CustomCarouselProps {
  items: CarouselItem[];
  autoPlay?: boolean;
  interval?: number;
}

const CustomCarousel: React.FC<CustomCarouselProps> = ({ items, autoPlay = true, interval = 4000 }) => {
  return (
    <Carousel autoPlay={autoPlay} interval={interval}>
      {items.map((item, i) => (
        <Paper key={i} style={{ padding: 24, textAlign: 'center', position: 'relative' }}>
          {item.image && (
            <img src={item.image} alt={item.title} style={{ maxWidth: '100%', maxHeight: 300, marginBottom: 16 }} />
          )}
          <h2>{item.title}</h2>
          <p>{item.description}</p>
          {item.buttonText && (
            <Button variant="contained" color="primary" onClick={item.onClick}>
              {item.buttonText}
            </Button>
          )}
        </Paper>
      ))}
    </Carousel>
  );
};

export default CustomCarousel;

